mapper.select:
select

from SYS_MENU where IS_HIDE=0 and MENU_ID in(
select RIGHT_ID from
SYS_RIGHT_CONFIG where 1=1 and RESOURCE_FLAG = '0'

	and ROLE_ID = #{roleId}


	and ROLE_ID in(select ur.ROLE_ID from SYS_USER_ROLE ur
	where
	ur.USER_ID=#{userId})

)

	and PARENT_ID = #{parentId}

order by MENU_LEVEL asc

select * from SYS_MENU
WHERE NAME LIKE '%${name}%'
${orderby}

select

from SYS_MENU where IS_HIDE='0'

	AND ${where}


	AND NAME = #{name}


	AND MENU_KEY = #{menuKey}


	AND MENU_ID= #{menuId}

mapper.update:
update SYS_MENU SET
PARENT_ID = #{parentId,jdbcType=VARCHAR}
,NAME= #{name,jdbcType=VARCHAR}
,MENU_KEY= #{menuKey,jdbcType=VARCHAR}
,MENU_URL= #{menuUrl,jdbcType=VARCHAR}
,TYPE= #{type,jdbcType=VARCHAR}
,DESCRIPTION= #{description,jdbcType=VARCHAR}
,IS_HIDE= #{isHide,jdbcType=VARCHAR}
WHERE MENU_ID = #{menuId,jdbcType=VARCHAR};

mapper.select:
SELECT
count(*) as count
FROM
SYS_MENU WHERE 1=1

	AND NAME = #{name}


	AND MENU_KEY = #{menuKey}


	AND MENU_ID != #{menuId}
